import "./SignUpPage.scss";
import { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

import SignUpInput from "../../components/SignUpInput/SignUpInput";

export default class SignUpPage extends Component {
  state = {
    error: "",
    success: false,
  };

  handleSubmit = (event) => {
    event.preventDefault();

    axios
      .post("http://localhost:8080/signup", {
        first_name: event.target.first_name.value,
        last_name: event.target.last_name.value,
        email: event.target.email.value,
        password: event.target.password.value,
        phone: event.target.phone.value,
        address: event.target.address.value,
        city: event.target.city.value,
        dog_name: event.target.dog_name.value,
        dog_info: event.target.dog_info.value,
      })
      .then(() => {
        this.setState({ success: true, error: "" });
        event.target.reset();
      })
      .catch((error) => {
        this.setState({ success: false, error: error.response.data });
      });
  };

  render() {
    return (
      <main className="SignUpPage">
        <form onSubmit={this.handleSubmit}>
          <SignUpInput placeholder="First Name" type="text" id="first_name" />
          <SignUpInput placeholder="Last Name" type="text" id="last_name" />
          <SignUpInput placeholder="Email" type="text" id="email" />
          <SignUpInput placeholder="Password" type="password" id="password" />
          <SignUpInput
            placeholder="Confirm Password"
            type="password"
            id="confirm_password"
          />
          <SignUpInput
            placeholder="Phone Number"
            type="text"
            id="phone_number"
          />

          <SignUpInput placeholder="Address" type="text" id="address" />

          <SignUpInput placeholder="City" type="text" id="City" />
          <SignUpInput placeholder="Dog Name" type="text" id="dog_name" />
          <textarea
            className="SignUpPage__dog-info"
            name="dogInfo"
            id="dog_info"
            rows="5"
            placeholder="Dogs information"
          ></textarea>
          {/* <Link to="/profile"> */}
          <button className="SignUpPage__button">Sign Up</button>
          {/* </Link> */}
        </form>
      </main>
    );
  }
}
